import base64
from io import BytesIO
from PIL import Image
import cv2
import numpy as np
from ddddocr import DdddOcr

ocr = DdddOcr(show_ad=False)

def pil_img(base64_str):
    image_data = base64.b64decode(base64_str.split(',')[1])
    img = Image.open(BytesIO(image_data)).convert("RGBA")
    background = Image.new("RGBA", img.size, (255, 255, 255, 255))
    composite = Image.alpha_composite(background, img)
    ocr_res = ocr.classification(composite)
    # composite.show()
    return ocr_res


def opencv_img(base64_str):
    img_data = base64.b64decode(base64_str.split(',')[1])
    nparr = np.frombuffer(img_data, np.uint8)
    img = cv2.imdecode(nparr, cv2.IMREAD_UNCHANGED)

    #处理透明通道 不然会很糊
    if img.shape[2] == 4:
        b, g, r, a = cv2.split(img)
        white_background = np.ones((img.shape[0], img.shape[1], 3), dtype=np.uint8) * 255
        for c in range(3):
            white_background[:, :, c] = b * (a / 255.0) + white_background[:, :, c] * (1 - a / 255.0)
            img = white_background
    # cv2.imshow('Image with Transparency', img)
    # cv2.waitKey(0)
    # cv2.destroyAllWindows()

    ocr_res = ocr.classification(Image.fromarray(img))

    return ocr_res

base64_str = "data:image/png;base64,:image/png;base64,"
print(pil_img(base64_str))
print(opencv_img(base64_str))




